document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".slider").forEach(l=>{var e=l.querySelectorAll(".slide");if(0<e.length&&1200<=window.innerWidth){const n=Math.ceil(e.length-4);let r=0;const t=()=>{var e=l.querySelector(".slider__container-field"),t=Math.min(Math.max(r,0),n);e.style.transform=`translateX(${`calc((-360px + (100vw - 1200px) * -0.333) * ${t})`})`},a=()=>{var e=l.querySelector(".slider-nav--prev"),t=l.querySelector(".slider-nav--next");e.classList.toggle("slider-nav--prev-active",0<r),t.classList.toggle("slider-nav--next-hidden",r===n)};l.querySelector(".slider-nav--next").addEventListener("click",()=>{r<n&&(r+=1,t(),a())}),l.querySelector(".slider-nav--prev").addEventListener("click",()=>{0<r&&(--r,t(),a())}),a()}})});